export default function DonutChart({ data }) {
  const total = Object.values(data).reduce((a, b) => a + b, 0);
  const colors = [
    "#ef4444",
    "#f97316",
    "#eab308",
    "#22c55e",
    "#3b82f6",
    "#8b5cf6",
  ];
  const labels = Object.keys(data);

  return (
    <div className="flex md:flex-row flex-col justify-center items-center gap-6">
      <div className="mt-4 grid grid-cols-2 gap-3">
        {labels.map((label, i) => {
          const color = colors[i % colors.length];
          return (
            <div
              key={label}
              className="flex items-start  gap-2 p-3 rounded-xl 
          bg-[var(--light-main-bg)] 
          border border-[color:var(--border-light)] 
          shadow-[0_1px_2px_0_rgba(16,24,40,0.05)]"
            >
              {renderIcon(label, color)}
              <div>
                <div className="font-medium text-xs leading-[150%] text-[color:var(--light-text-text-secondary)] font-family">
                  {label}
                </div>
                <div className="font-semibold text-[22px] leading-[145%] text-[color:var(--light-text-text-primary)] font-family mt-0.5">
                  {data[label]}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      <div>
        <img src={Group1} alt="" />
      </div>
    </div>
  );
}
